import axios from "axios";
import printValue from "yup/es/util/printValue";

const API_URL = "http://localhost:8000/api/v1/accounts/login/";

class AuthService {

  login(username: string, password: string) {
    const loginBody = JSON.stringify({ username: username, password: password });
    var customConfig = {
      headers: {
        'Content-Type': 'application/json'
      }
    };

    // printValue(axios.post(API_URL, loginBody, customConfig));

    return axios
      .postForm(API_URL, loginBody, customConfig)
      .then(response => {
        if (response.data.accessToken) {
          localStorage.setItem("user", JSON.stringify(response.data));
        }
        return response.data;
      });
  }

  logout() {
    localStorage.removeItem("user");
  }

  register(username: string, email: string, password: string) {
    return axios.post(API_URL + "signup", {
      username,
      email,
      password
    });
  }

  getCurrentUser() {
    const userStr = localStorage.getItem("user");
    if (userStr) return JSON.parse(userStr);

    return null;
  }
}

export default new AuthService();
