import React, { Component } from "react";
import { Navigate } from "react-router-dom";
import IUser from "../types/user.type";
import ReportService from "../services/report.service";
import AuthService from "../services/auth.service";
import {array} from "yup";

type Props = {};

type State = {
  redirect: string | null,
  userReady: boolean,
  currentUser: IUser & { token: string },
}
export default class Reports extends Component<Props, State> {
  public allReports: any;

  constructor(props: Props) {
    super(props);

    this.allReports = ReportService.getAllReports();

    this.state = {
      redirect: null,
      userReady: false,
      currentUser: { token: "" },
    };
  }

  componentDidMount() {
    const currentUser = AuthService.getCurrentUser();

    if (!currentUser) this.setState({ redirect: "/login" });
    this.setState({ currentUser: currentUser, userReady: true })
  }

  render() {
    if (this.state.redirect) {
      return <Navigate to={this.state.redirect}/>
    }

    const {currentUser} = this.state;
    // const { allReports } = this.state;

    {
      for (const report of this.allReports) {
        console.log(report)
      }

      // @ts-ignore
      return (
          <div className="reports_body">
            {(this.state.userReady) ?
                <div>
                  <div className="row">
                    <div className="col-3 sidepannel">
                      <div className="secondary_text">
                        MY REPORTS
                      </div>
                      {/*{this.render_names_list()}*/}

                      <button type="submit" className="btn singin_button">
                        <span>ADD A REPORT</span>
                      </button>
                    </div>
                    <div className="col">
                      <header className="jumbotron">
                        <h3>
                          <strong>{currentUser.username}</strong> Reports
                        </h3>
                      </header>
                      <p>
                        <strong>token:</strong>{" "}
                        {currentUser.token.substring(0, 20)} ...{" "}
                        {currentUser.token.substr(currentUser.token.length - 20)}
                      </p>
                      <p>
                        <strong>Id:</strong>{" "}
                        {currentUser.id}
                      </p>
                      <p>
                        <strong>Email:</strong>{" "}
                        {currentUser.email}
                      </p>
                      <strong>Authorities:</strong>
                      <ul>
                        {currentUser.roles &&
                            currentUser.roles.map((role, index) => <li key={index}>{role}</li>)}
                      </ul>
                    </div>
                  </div>

                </div> : null}
          </div>
      );
    }
  }
}
