import axios from "axios";

const API_URL = "http://localhost:8000/api/v1/reports";
const API_URL_LOGIN = "http://localhost:8000/api/v1/accounts/login";

class ReportService {
    getAllReports() {
        const userStr = localStorage.getItem("user");
        let user = null;
        if (userStr)
            user = JSON.parse(userStr);

        const login_user_data = JSON.stringify({username: user.username, password: user.password});

        return axios
            .post(API_URL + "login", login_user_data)
            .then(response => {
                console.log(response.data);
                debugger
                if (response.data.token) {
                    axios.get(API_URL, {
                        headers:{
                            Authorization: 'Bearer ' + response.data.token,
                        },
                    });
                }
            })
    }
}

export default new ReportService();
